Bash(cd D:/workspace_py/multiVibe && node -e "
const https = require\('https'\);
const token = process.env.GH_TOKEN || \(\(\) => {
  const fs = require\('fs'\);
  const p = require\('path'\).join\(__dirname, '.env.json'\);
  if \(fs.existsSync\(p\)\) return JSON.parse\(fs.readFileSync\(p,'utf8'\)\).GH_TOKEN;
  return '';
}\)\(\);

https.get\({
  hostname: 'api.github.com',
  path: '/repos/kccitsales/hivecode/releases',
  headers: { 'Authorization': 'token '+token, 'User-Agent': 'hivecode-build' }
}, res => {
  let body = '';
  res.on\('data', d => body += d\);
  res.on\('end', \(\) => {
    const releases = JSON.parse\(body\);
    const draft = releases.find\(r => r.tag_name === 'v1.0.8' && r.draft\);
    if \(!draft\) { console.log\('Release already published or not found as draft'\); return; }
    const data = JSON.stringify\({ draft: false, tag_name: 'v1.0.8', make_latest: 'true' }\);
    const req = https.request\({
      hostname: 'api.github.com',
      path: '/repos/kccitsales/hivecode/releases/'+draft.id,
      method: 'PATCH',
      headers: { 'Authorization': 'token '+token, 'Content-Type': 'application/json', 'User-Agent': 'hivecode-build', 'Content-Length': Buffer.byteLength\(data\) }
    }, res2 => {
      let b = '';
      res2.on\('data', d => b += d\);
      res2.on\('end', \(\) => {
        const r = JSON.parse\(b\);
        console.log\(r.draft === false ? 'Release published: '+r.html_url : 'Publish failed: '+\(r.message||'unknown'\)\);
      }\);
    }\);
    req.write\(data\);
    req.end\(\);
  }\);
}\);
